mapper.select:
SELECT  
  id,
  code,
  name,
  memo,
  type,
  date_create,
  date_update,
  valid
FROM sys_permission


     id=#{id}


     AND code=#{code}


     AND name=#{name}


     AND memo=#{memo}


     AND date_create=#{dateOfCreate}


     AND date_update=#{dateOfUpdate}


     AND is_valid=#{isValid}

SELECT id, code, name, memo, type, (CASE WHEN type = 1 THEN '菜单' ELSE '其它' END) AS typeName, date_create, date_update, valid, (CASE WHEN valid = 1 THEN '启用' ELSE '停用' END) AS status, (select realName from ums_user u where u.id = p.creator_fk ) as creator FROM sys_permission p
name LIKE CONCAT(CONCAT('%',#{name}),'%')
AND code=#{code}
AND type=#{type}
AND valid=#{isValid}
order by code
